function handleClick(){var e=new XMLHttpRequest,n=document.querySelector("#country").value,t="http://localhost:3000/searchQuery?country="+n;e.open("GET",t,!0);var r="";e.addEventListener("load",function(n){if(n.preventDefault(),e.status>=200&&e.status<=400){for(var t=JSON.parse(e.responseText),a=0;a<t.length;a++)r=r+"<div>"+t[a].name+" "+t[a].year+' <a href = "'+t[a].link+'">Click Here</a></div> ';answer.innerHTML=r}}),e.send()}var btn=document.querySelector("#country-submit");btn.addEventListener("click",handleClick);var answer=document.querySelector("#answer");$(document).ready(function(){$("#link").on("input",function(){var e=$(this),n=/^((https|http)?:\/\/)/,t=n.test(e.val());div=document.querySelector("#aboutLink"),t?div.innerText="Valid Link":div.innerText="Invalid Link!"})});